from pathlib import Path
import json

for i in range(1, 58):
    path = Path('tasks.json')
    WHOLE_THEORY = json.loads(path.read_text(encoding='utf-8'))
    WHOLE_THEORY[f"task_{1}"][f"quiz_{i}"] = {
        "task_number": 1,
        "quiz_number": i,
        "photo_of_answer_options": None,
        "quiz_question": f"Tasks_materials\\questions_for_quizzes\\task_1\\quiz_{i}",
        "quiz_condition": WHOLE_THEORY[f"task_{1}"][f"quiz_{i}"]["photo_of_answer_options"],
        "correct_answer": WHOLE_THEORY[f"task_{1}"][f"quiz_{i}"]["correct_answer_to_the_quiz"],
        "quiz_explanation": None
    }
    path.write_text(json.dumps(WHOLE_THEORY, indent=4, ensure_ascii=False), encoding='utf-8')

